import { ImageBackground,View,Text,StyleSheet,Image,ActivityIndicator} from "react-native"
import { Entypo } from '@expo/vector-icons'; 

 export default function Header ({navigation}){
   
    
        return(
            
            <View style={{flexDirection:"row-reverse",backgroundColor:"#E09FAD"}} >
            <Entypo name="home" size={24} color="black" style={{margin:20,color:"white"}} />

            <Text style={{fontSize:20,marginVertical:20,color:"white"}}>أخبار السيارات الكهربية </Text>
            <Entypo name="home" size={24} color="black" style={{margin:20,color:"white",position:"absolute"}} />
            </View>
        )
    }
