import React from "react"
import { ImageBackground,View} from "react-native"

 export default function SplashScreen ({navigation}){
     const goToHome=()=>{
        setTimeout(() => {
            navigation.navigate('Home')
         }, 2000);

     }
    
        return(
            <View>
            {goToHome()}
            <ImageBackground style={{flex:1}} source={require('../..')} />
            </View>
        )
    }
