import { Text,View ,Button,Image} from "react-native"

export default function Card() {
        return(
            <View style={{display:"flex",alignItems:"flex-start",flexDirection:"row"}}>
                <Text style={{fontSize:14,marginVertical:20}}>أيلون ماسك يطرح سيارة تسلا جديده</Text>
                <Image source={require("../../assets/2.jpg")} style={{width:200,height:200}}/>
                
            </View>
        )
    }
